CREATE TABLE `EGPSDAT` (
  `datId` smallint(6) NOT NULL AUTO_INCREMENT,
  `indxNo` smallint(6) DEFAULT NULL,
  `datType` varchar(1) DEFAULT NULL,
  `label` varchar(128) DEFAULT NULL,
